mapper.select:
SELECT 
FROM base_role r
where r.dr = 0


        and r.`name` like CONCAT('%',#{condition.searchMap.name},'%')


        and r.`code` like CONCAT('%',#{condition.searchMap.code},'%')


        and r.comments like CONCAT('%',#{condition.searchMap.comments},'%')



        and r.create_time >= #{condition.searchMap.createTimeList[0]}
        and r.create_time <= #{condition.searchMap.createTimeList[1]}



        and r.create_user like CONCAT('%', #{condition.searchMap.createUser}, '%')



        and r.last_modified >= #{condition.searchMap.modifyTimeList[0]}
        and r.last_modified <= #{condition.searchMap.modifyTimeList[1]}



        and r.last_modify_user like CONCAT('%', #{condition.searchMap.lastModifyUser}, '%')


order by r.last_modified desc

select 
from base_role r
where r.id = #{id}

mapper.update:
update base_role
set dr=1,
    `version` = #{version} + 1
where id=#{id}
and `version` = #{version}
